mapper.insert:
INSERT INTO T_USER
       (
         ID
       , NAME
       , EMAIL
       , PHONE
       , BIRTH
       , PASSWORD
       )
  VALUES
       (
         #{id}
       , #{name}
       , #{email}
       , #{phone}
       , #{birth}
       , #{password}
       )

mapper.update:
UPDATE T_USER
   SET NAME  = #{name}
     , EMAIL = #{email}
     , PHONE = #{phone}
     , BIRTH = #{birth}
 WHERE ID = #{id}
